// src/pages/home.jsx
import React, { useEffect, useState } from "react";
import { api } from "../utils/api.js";
import ProductCard from "../components/productCard.jsx";
import Loader from "../components/loader.jsx";

export default function Home() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  async function load() {
    try {
      const data = await api("/products");
      setItems(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Home load error", err);
    }
    setLoading(false);
  }

  useEffect(() => {
    load();
  }, []);

  return (
    <div className="w-full">

      {/* ⭐ HERO SECTION */}
      <section
        className="relative w-full h-[65vh] bg-cover bg-center flex items-center justify-center"
        style={{
          backgroundImage:
            "url('../assets/kalogo.png')",
        }}
      >
        <div className="absolute inset-0 bg-black/50"></div>

        <div className="relative z-10 text-center text-gray px-6 max-w-2xl">
          <h1 className="text-5xl font-extrabold drop-shadow-lg">
            Buy. Donate. Sell. Save. Sustain.
          </h1>
          <p className="mt-4 text-xl opacity-90">
            Discover great deals and give pre-loved items a second life.
          </p>

          <a
            href="/browse"
            className="mt-8 inline-block bg-green-500 hover:bg-green-600 transition text-white px-8 py-3 rounded-full text-lg font-medium shadow-lg"
          >
            Browse Listings
          </a>
        </div>
      </section>

      {/* ⭐ FEATURES / VALUE PROPOSITION */}
      <section className="max-w-6xl mx-auto px-6 py-12 grid grid-cols-1 sm:grid-cols-3 gap-8 text-center">
        {[
          {
            title: "Quality Services",
            img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWUnNk-ZKhmFRsjg-jB_tqoVqHN8nHYkcm7Q&s"
          },
          {
            title: "Eco Friendly",
            img: "data:image/png;base64,"
          },
          {
            title: "Affordable Deals",
            img: "data:image/png;base64,"
          }
        ].map((f) => (
          <div
            key={f.title}
            className="bg-white shadow-sm border rounded-xl p-6 hover:shadow-md transition"
          >
            <img src={f.img} className="mx-auto mb-4 h-16 opacity-80" />
            <h3 className="text-lg font-bold">{f.title}</h3>
          </div>
        ))}
      </section>

      {/* ⭐ POPULAR CATEGORIES */}
      <section className="max-w-6xl mx-auto px-6 mt-12">
        <h2 className="text-2xl font-bold mb-6">Popular Categories</h2>

        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 gap-4">
          {[
            { name: "Electronics", icon: "data:image/png;base64," },
              { name: "Furniture", icon: "data:image/png;base64," },
              { name: "Phones", icon: "data:image/png;base64," },
              { name: "Clothing", icon: "data:image/png;base64," },
            { name: "Books", icon: "data:image/jpeg;base64," }
          ].map((cat) => (
            <div
              key={cat.name}
              className="p-4 bg-white border rounded-xl shadow-sm hover:shadow-md text-center cursor-pointer"
            >
              <img src={cat.icon} className="mx-auto h-12 mb-2 opacity-80" />
              {cat.name}
            </div>
          ))}
        </div>
      </section>

      {/* ⭐ PRODUCT GRID */}
      <section className="max-w-6xl mx-auto px-6 mt-16 mb-20">
        <h2 className="text-2xl font-bold mb-4">Latest Listings</h2>

        {loading ? (
          <Loader />
        ) : items.length === 0 ? (
          <p>No items listed yet.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
            {items.map((p) => (
              <ProductCard key={p._id} item={p} />
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
